import React, { useState, useEffect } from 'react';
import { TableCustomPagination } from './TableCustomPagination'; // Dosya yolunu güncelleyin
import axios from 'axios';
import { Button, Typography, Box } from '@mui/material';

export function ZimmetPage() {
  // State tanımlamaları
  const [data, setData] = useState([]);
  const [totalSize, setTotalSize] = useState(0);
  const [page, setPage] = useState(0);
  const [pageSize, setPageSize] = useState(10);
  const [order, setOrder] = useState({ columnName: '', sort: 'asc' });
  const [isLoading, setIsLoading] = useState(true);
  const [checkList, setCheckList] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  // Veriyi çekme
  const fetchData = async () => {
    setIsLoading(true);
    try {
      const response = await axios.get('/api/zimmet', {
        params: {
          page,
          pageSize,
          sort: `${order.sort},${order.columnName}`,
          search: searchTerm,
        },
      });
      setData(response.data.items);
      setTotalSize(response.data.totalSize);
    } catch (error) {
      console.error('Veri çekme hatası:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // Sayfa değişimi
  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  // Sayfa boyutu değişimi
  const handleChangeRowsPerPage = (event) => {
    setPageSize(parseInt(event.target.value, 10));
    setPage(0);
  };

  // Sıralama değişikliği
  const handleSortChange = (sortInfo) => {
    setOrder(sortInfo);
  };

  // Arama işlemi
  const handleSearch = () => {
    setPage(0);
    fetchData();
  };

  // Etkiler: veri ve arama terimi güncellemeleri
  useEffect(() => {
    fetchData();
  }, [page, pageSize, order, searchTerm]);

  const columns = [
    { field: 'id', headerName: 'ID', sortable: true },
    { field: 'item', headerName: 'Ürün', sortable: true },
    { field: 'quantity', headerName: 'Miktar', sortable: true },
    // Diğer sütunlarınızı ekleyin
  ];

  return (
    <Box sx={{ p: 2 }}>
      <Typography variant="h6">Zimmetler</Typography>
      <Box sx={{ mb: 2 }}>
        <input
          type="text"
          placeholder="Ara..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <Button onClick={handleSearch} variant="contained" sx={{ ml: 2 }}>
          Ara
        </Button>
      </Box>
      <TableCustomPagination
        columns={columns}
        data={data}
        totalSize={totalSize}
        onChangePage={handleChangePage}
        page={page}
        pageSize={pageSize}
        onSortChange={handleSortChange}
        order={order}
        isLoading={isLoading}
        notPagination={false}
        isSelected={true}
        checkList={checkList}
        setCheckList={setCheckList}
        isSticky={true}
        minHeight="400px"
      />
    </Box>
  );
}
