import axiosInstance from '../core/axiosInstance';

export const getPersonel = () => {
  return axiosInstance.get(`/personel`)   //Yaratılan axios instance üzerinden istekler yapılacak. Birde url in baş kısmı her apide aynı olduğu için onu base url altına instance ın içine yazmıştık, url in devamını bu şekilde yazmamız yetiyor.
		.then(response => {
			console.log('Resonse:', response.data);
		})
		.catch(error => {
			console.error('Error:', error);
		});
};


export const addPersonalItem = async (personalData) => {
    try {
        const response = await axiosInstance.post('/personel', personalData) // axiosInstance üzerinden POST isteği yapar ve personalData verilerini gönderir.
            ;
        console.log('Response:', response.data); // Başarılı yanıtın veri kısmını konsola yazdırır.
    } catch (error) {
        console.error('Error:', error); // Hata durumunda hatayı konsola yazdırır.
    }
};
