import React, { useEffect, useState } from 'react';
import { getZimmetlerByPersonel, geriAlZimmet } from '../services/zimmetServices';

const ZimmetDetailPage = () => {
  const [zimmetler, setZimmetler] = useState([]);
  const [selectedZimmet, setSelectedZimmet] = useState(null);
  const [returnDate, setReturnDate] = useState('');
  const personelSicilNo = window.location.pathname.split('/')[2]; // URL'den Sicil No'yu al

  useEffect(() => {
    const fetchZimmetler = async () => {
      try {
        const response = await getZimmetlerByPersonel(personelSicilNo);
        setZimmetler(response.data);
      } catch (error) {
        console.error('Zimmet bilgileri çekme hatası:', error);
      }
    };

    fetchZimmetler();
  }, [personelSicilNo]);

  const handleGeriAl = async () => {
    try {
      await geriAlZimmet(selectedZimmet, returnDate);
      setReturnDate('');
      setSelectedZimmet(null);
      // Optionally, refresh the data
    } catch (error) {
      console.error('Zimmet geri alma hatası:', error);
    }
  };

  return (
    <div>
      <h1>Zimmet Detayları</h1>
      {zimmetler.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Envanter Tipi</th>
              <th>Marka</th>
              <th>Model</th>
              <th>İşlem</th>
            </tr>
          </thead>
          <tbody>
            {zimmetler.map((zimmet) => (
              <tr key={zimmet.zimmetId}>
                <td>{zimmet.envanterTipi}</td>
                <td>{zimmet.marka}</td>
                <td>{zimmet.model}</td>
                <td>
                  <button onClick={() => setSelectedZimmet(zimmet.zimmetId)}>Geri Al</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Zimmet bilgisi bulunamadı.</p>
      )}
      {selectedZimmet && (
        <div>
          <h2>Zimmeti Geri Al</h2>
          <label>
            Teslim Alınan Tarih:
            <input type="date" value={returnDate} onChange={(e) => setReturnDate(e.target.value)} />
          </label>
          <button onClick={handleGeriAl}>Geri Al</button>
          <button onClick={() => setSelectedZimmet(null)}>İptal</button>
        </div>
      )}
    </div>
  );
};

export default ZimmetDetailPage;
