import React, { useEffect, useState } from 'react';
import { getZimmetlerByPersonel, geriAlZimmet } from '../services/zimmetService';

const ZimmetDetailPage = () => {
  const [zimmetler, setZimmetler] = useState([]);
  const personelSicilNo = window.location.pathname.split('/')[2];

  useEffect(() => {
    const fetchZimmetler = async () => {
      try {
        const response = await getZimmetlerByPersonel(personelSicilNo);
        setZimmetler(response.data);
      } catch (error) {
        console.error('Zimmet bilgileri getirilirken hata oluştu:', error);
      }
    };
    fetchZimmetler();
  }, [personelSicilNo]);

  const handleGeriAl = async (zimmetId) => {
    try {
      await geriAlZimmet(zimmetId);
      setZimmetler(zimmetler.filter((zimmet) => zimmet.zimmetId !== zimmetId));
    } catch (error) {
      console.error('Zimmet geri alınırken hata oluştu:', error);
    }
  };

  if (!zimmetler.length) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Zimmet Detayları</h1>
      <table>
        <thead>
          <tr>
            <th>Envanter Tipi</th>
            <th>Marka</th>
            <th>Model</th>
            <th>İşlem</th>
          </tr>
        </thead>
        <tbody>
          {zimmetler.map((zimmet) => (
            <tr key={zimmet.zimmetId}>
              <td>{zimmet.envanter.tip}</td>
              <td>{zimmet.envanter.marka}</td>
              <td>{zimmet.envanter.model}</td>
              <td>
                <button onClick={() => handleGeriAl(zimmet.zimmetId)}>Zimmeti Geri Al</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ZimmetDetailPage;
