import React, { useState } from "react";
import {
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
} from "@mui/material";
import * as services from "../services/zimmetServices";
import { filterPersonel } from "../services/zimmetServices";
import ZimmetDetailPage from "../ZimmetYonetim/ZimmetDetailPage";

const PersonelSearchPage = () => {
  const [sicilNo, setSicilNo] = useState("");
  const [name, setName] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [personelSicilNo, setPersonelSicilNo] = useState();

  const handleSearch = () => {
    filterPersonel(sicilNo, name)
      .then((res) => {
        setSearchResults(res.data);
        setPersonelSicilNo(null)
      })
      .catch((error) => {
        console.error("Personel arama hatası:", error);
      });
  };

  return (
    <div>
      <h1>Personel Ara</h1>
      <div>
        <label>
          Sicil No:
          <input
            type="text"
            value={sicilNo}
            onChange={(e) => setSicilNo(e.target.value)}
          />
        </label>
        <label>
          Ad:
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </label>
        <button onClick={handleSearch}>Ara</button>
      </div>
      {searchResults?.length > 0 && (
        <div>
          <h2>Arama Sonuçları</h2>
          <table>
            <thead>
              <tr>
                <th>Sicil No</th>
                <th>Ad</th>
                <th>Soyad</th>
                <th>Birim</th>
                <th>Görev</th>
                <th>İşlem</th>
              </tr>
            </thead>
            <tbody>
              {searchResults.map((personel) => (
                <tr key={personel.personelSicilNo}>
                  <td>{personel.personelSicilNo}</td>
                  <td>{personel.personelName}</td>
                  <td>{personel.personelSurname}</td>
                  <td>{personel.personelBirimi}</td>
                  <td>{personel.personelGorevi}</td>
                  <td>
                    <button
                      onClick={() => {
                        setPersonelSicilNo(personel.personelSicilNo);
                      }}
                      type="button"
                    >
                      Zimmet İşlemleri
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {personelSicilNo && (
        <div style={{marginTop:"50px"}}>
        <ZimmetDetailPage personelSicilNo={personelSicilNo} />
        </div>
      )}
    </div>
  );
};

export default PersonelSearchPage;
