import React, { useState, useEffect } from 'react';
import { getZimmetlerByPersonel, geriAlZimmet } from '../services/zimmetServices';
import * as envanterServices from '../services/envanterServices';
import * as personelServices from '../services/personelServices';

const ZimmetModal = ({ personelSicilNo, onClose }) => {
  const [zimmetList, setZimmetList] = useState([]);
  const [returnDate, setReturnDate] = useState('');

  useEffect(() => {
    const fetchZimmetler = async () => {
      try {
        const response = await getZimmetlerByPersonel(personelSicilNo);
        setZimmetList(response.data);
      } catch (error) {
        console.error('Zimmetler alınırken hata:', error);
      }
    };

    fetchZimmetler();
  }, [personelSicilNo]);

  const handleGeriAl = async (zimmetId) => {
    try {
      await geriAlZimmet(zimmetId);
      const response = await getZimmetlerByPersonel(personelSicilNo);
      setZimmetList(response.data);
    } catch (error) {
      console.error('Zimmet geri alma hatası:', error);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Zimmet Detayları</h2>
        <button onClick={onClose}>Kapat</button>

        <table>
          <thead>
            <tr>
              <th>Envanter Tipi</th>
              <th>Envanter Markası</th>
              <th>Envanter Modeli</th>
              <th>İşlem</th>
            </tr>
          </thead>
          <tbody>
            {zimmetList.map((zimmet) => (
              <tr key={zimmet.zimmetId}>
                <td>{zimmet.envanterDto.tip}</td>
                <td>{zimmet.envanterDto.envanterMarkasi}</td>
                <td>{zimmet.envanterDto.envanterModel}</td>
                <td>
                  <button onClick={() => handleGeriAl(zimmet.zimmetId)}>Geri Al</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* Additional functionality for adding new zimmet can be added here */}
      </div>
    </div>
  );
};

export default ZimmetModal;
