import React, { useState } from 'react';
import { Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';

import { filterPersonel } from '../services/zimmetServices';

const PersonelSearchPage = () => {
  const [sicilNo, setSicilNo] = useState('');
  const [name, setName] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await filterPersonel(sicilNo, name);
      setSearchResults(response.data);
    } catch (error) {
      console.error('Personel arama hatası:', error);
    }
  };

  const handleZimmetIslemleri = (sicilNo) => {
    window.location.href = `/personel/${sicilNo}/zimmetler`;
  };

  return (
    <div>
      <h1>Personel Ara</h1>
      <div>
        <label>
          Sicil No:
          <input type="text" value={sicilNo} onChange={(e) => setSicilNo(e.target.value)} />
        </label>
        <label>
          Ad:
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </label>
        <button onClick={handleSearch}>Ara</button>
      </div>
      {searchResults.length > 0 && (
        <div>
          <h2>Arama Sonuçları</h2>
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Sicil No</TableCell>
                  <TableCell>Ad</TableCell>
                  <TableCell>Soyad</TableCell>
                  <TableCell>Birim</TableCell>
                  <TableCell>İşlem</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {searchResults.map((personel) => (
                  <TableRow key={personel.sicilNo}>
                    <TableCell>{personel.sicilNo}</TableCell>
                    <TableCell>{personel.name}</TableCell>
                    <TableCell>{personel.surname}</TableCell>
                    <TableCell>{personel.birim}</TableCell>
                    <TableCell>
                      <Button onClick={() => handleZimmetIslemleri(personel)}>Zimmet İşlemleri</Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </div>
      )}
    </div>
  );
};


export default PersonelSearchPage;
