import React, { useState, useEffect } from 'react';
import {
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
} from '@mui/material';
import * as zimmetServices from '../services/zimmetServices';
import * as envanterServices from '../services/envanterServices';
import * as personelServices from '../services/personelServices';
import ZimmetSelect from './ZimmetSelect';

const ZimmetDetailPage = ({ personelSicilNo }) => {
  const [zimmetList, setZimmetList] = useState([]);
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedZimmet, setSelectedZimmet] = useState(null);
  const [envanterList, setEnvanterList] = useState([]);
  const [personelList, setPersonelList] = useState([]);

  useEffect(() => {
    const fetchZimmetler = async () => {
      try {
        const response = await zimmetServices.getZimmetlerByPersonel(personelSicilNo);
        setZimmetList(response.data.filter(zimmet => zimmet.status === 'PERSONEL'));
      } catch (error) {
        console.error('Zimmet verileri çekme hatası:', error);
      }
    };

    if (personelSicilNo) {
      fetchZimmetler();
      envanterServices.getEnvanter().then(response => setEnvanterList(response.data));
      personelServices.getPersonel().then(response => setPersonelList(response.data));
    }
  }, [personelSicilNo]);

  const handleGeriAl = async (zimmetId) => {
    try {
      await zimmetServices.geriAlZimmet(zimmetId);
      const response = await zimmetServices.getZimmetlerByPersonel(personelSicilNo);
      setZimmetList(response.data.filter(zimmet => zimmet.status === 'PERSONEL'));
    } catch (error) {
      console.error('Geri alma işlemi hatası:', error);
    }
  };

  const handleOpenDialog = () => {
    setSelectedZimmet(null);
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setSelectedZimmet(null);
  };

  const handleSubmit = async (newZimmet) => {
    try {
      await zimmetServices.createZimmet(newZimmet);
      const response = await zimmetServices.getZimmetlerByPersonel(personelSicilNo);
      setZimmetList(response.data.filter(zimmet => zimmet.status === 'PERSONEL'));
      handleCloseDialog();
    } catch (error) {
      console.error('Yeni zimmet ekleme hatası:', error);
    }
  };

  return (
    <div>
      <h1>Zimmet Detayları</h1>
      <Button variant="contained" color="primary" onClick={handleOpenDialog}>
        Yeni Zimmet Ekle
      </Button>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Envanter Tipi</TableCell>
              <TableCell>Envanter Markası</TableCell>
              <TableCell>Envanter Modeli</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {zimmetList.map(zimmet => (
              <TableRow key={zimmet.zimmetId}>
                <TableCell>{zimmet?.tip}</TableCell>
                <TableCell>{zimmet?.envanterMarka}</TableCell>
                <TableCell>{zimmet?.envanterModel}</TableCell>
                <TableCell>
                  <Button onClick={() => handleGeriAl(zimmet.zimmetId)}>
                    Geri Al
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <ZimmetSelect
        open={openDialog}
        onClose={handleCloseDialog}
        onSubmit={handleSubmit}
        envanterList={envanterList}
        personelList={personelList}
      />
    </div>
  );
};

export default ZimmetDetailPage;
