import axiosInstance from './axiosInstance';

export const getPersonel = () => {
  return axiosInstance.get(`http://localhost:8080/personel`)   //Yaratılan axios instance üzerinden istekler yapılacak. Birde url in baş kısmı her apide aynı olduğu için onu base url altına instance ın içine yazmıştık, url in devamını bu şekilde yazmamız yetiyor.
		.then(response => {
			console.log('Resonse:', response.data);
		})
		.catch(error => {
			console.error('Error:', error);
		});
};


export const addPersonalItem = (personalData) => {
    return axiosInstance.post('/personal', personalData)   // axiosInstance üzerinden POST isteği yapar ve personalData verilerini gönderir.
        .then(response => {
            console.log('Response:', response.data);  // Başarılı yanıtın veri kısmını konsola yazdırır.
        })
        .catch(error => {
            console.error('Error:', error);  // Hata durumunda hatayı konsola yazdırır.
        });
};
