import "./App.css";
import LeftBar from "./containers/LeftBar";
import Content from "./containers/Content";
import Header from "./containers/Header";
import PersonelSearchPage from './PersonelYonetim/PersonelSearchPage';
import ZimmetDetailPage from './ZimmetYonetim/ZimmetDetailPage';
import Dashboard from './page/Dashboard';
import LoginPage from './page/LoginPage'; // LoginPage bileşenini eklemeyi unutmayın

function App() {
  let page;
  const path = window.location.pathname;
  const isLogin = localStorage.getItem("isLogin") === 'true';

  if (!isLogin) {
    page = <LoginPage />;
  } else {
    if (path === '/zimmet-page') {
      page = <PersonelSearchPage />;
    } else if (path.startsWith('/zimmet/personel/') && path.endsWith('/zimmet')) {
      page = <ZimmetDetailPage />;
    } else if (path === '/') {
      page = <Dashboard />;
    } else {
      page = <Dashboard />;
    }
  }

  return (
    <div style={{ height: "100%" }}>
      {isLogin ? (
        <>
          <Header id="header" />
          <LeftBar id="leftbar" />
          <Content id="content">{page}</Content>
        </>
      ) : (
        page
      )}
    </div>
  );
}

export default App;
