            label="Ayrılma Nedeni"
            type="text"
            fullWidth
            variant="outlined"
            value={newPersonel.ayrilmaNedeni}
            onChange={handleChange}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            İptal
          </Button>
          <Button onClick={handleCreatePersonel} color="primary">
            Ekle
          </Button>
        </DialogActions>
      </Dialog>
