import React, { useState, useEffect } from "react";
import * as services from "../services/zimmetServices";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TablePagination from "@mui/material/TablePagination";
import TableRow from "@mui/material/TableRow";
import Button from "@mui/material/Button";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import TextField from "@mui/material/TextField";
import MenuItem from "@mui/material/MenuItem";
import Select from "@mui/material/Select";

const columns = [
  { id: 'zimmetId', label: 'Zimmet Id', minWidth: 170 },
  { id: 'statu', label: 'Statu', minWidth: 100 },
  { id: 'personelSicilNo', label: 'Personel SicilNo', minWidth: 170 },
  { id: 'personelName', label: 'Personel Adı', minWidth: 170 },
  { id: 'personelSurname', label: 'Personel Soyadı', minWidth: 170 },
  { id: 'personelBirimi', label: 'Personel Birimi', minWidth: 170 },
  { id: 'personelGorevi', label: 'Personel Görevi', minWidth: 170 },
  { id: 'envanterTipi', label: 'Envanter Tipi', minWidth: 170 },
  { id: 'envanterMarkasi', label: 'Envanter Markası', minWidth: 170 },
  { id: 'envanterModeli', label: 'Envanter Modeli', minWidth: 170 },
  { id: 'actions', label: 'Actions', minWidth: 170 },
];

export default function ZimmetPage() {
  const [zimmetList, setZimmetList] = useState([]);
  const [availableEnvanter, setAvailableEnvanter] = useState([]);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [open, setOpen] = useState(false);
  const [newZimmetOpen, setNewZimmetOpen] = useState(false);
  const [selectedZimmet, setSelectedZimmet] = useState(null);
  const [selectedEnvanter, setSelectedEnvanter] = useState('');
  const [envanterAlimTarihi, setEnvanterAlimTarihi] = useState('');

  useEffect(() => {
    services.getZimmet().then((res) => {
      setZimmetList(res.data);
    });
  }, []);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  const handleClickOpen = (zimmet) => {
    setSelectedZimmet(zimmet);
    setOpen(true);
  };

  const handleNewZimmetOpen = () => {
    services.getAvailableEnvanter().then((res) => {
      setAvailableEnvanter(res.data);
    });
    setNewZimmetOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setSelectedZimmet(null);
    setEnvanterAlimTarihi('');
  };

  const handleNewZimmetClose = () => {
    setNewZimmetOpen(false);
    setSelectedEnvanter('');
    setEnvanterAlimTarihi('');
  };

  const handleReturnZimmet = () => {
    // TODO: Implement the logic to handle zimmet return
    console.log(`Returning zimmet ID: ${selectedZimmet?.zimmetId}`);
    handleClose();
  };

  const handleCreateZimmet = () => {
    // TODO: Implement the logic to handle zimmet creation
    console.log(`Creating zimmet for envanter ID: ${selectedEnvanter} on ${envanterAlimTarihi}`);
    handleNewZimmetClose();
  };

  return (
    <Paper sx={{ width: "100%", overflow: "hidden" }}>
      <Button variant="contained" color="primary" onClick={handleNewZimmetOpen}>
        Yeni Zimmet Ekle
      </Button>
      <TableContainer sx={{ maxHeight: 440 }}>
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {zimmetList
              .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
              .map((row) => (
                <TableRow hover role="checkbox" tabIndex={-1} key={row.zimmetId}>
                  {columns.map((column) => {
                    let value;
                    if (column.id === 'actions') {
                      return (
                        <TableCell key={column.id} align={column.align}>
                          <Button
                            variant="contained"
                            color="secondary"
                            onClick={() => handleClickOpen(row)}
                          >
                            Geri Al
                          </Button>
                        </TableCell>
                      );
                    } else if (column.id.startsWith('personel')) {
                      value = row.personelDto ? row.personelDto[column.id.replace('personel', 'personel')] : '';
                    }  else if (column.id.startsWith('envanter')) {
                      value = row.envanterDto ? row.envanterDto[column.id.replace('envanter', 'envanter')] : '';
                    } else {
                      value = row[column.id];
                    }
                    return (
                      <TableCell key={column.id} align={column.align}>
                        {value}
                      </TableCell>
                    );
                  })}
                </TableRow>
              ))}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        rowsPerPageOptions={[10, 25, 100]}
        component="div"
        count={zimmetList.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
      />
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Zimmeti Geri Al</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Lütfen zimmetin geri alınma tarihini seçin.
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="returnDate"
            label="Geri Alım Tarihi"
            type="date"
            fullWidth
            variant="outlined"
            value={envanterAlimTarihi}
            onChange={(e) => setEnvanterAlimTarihi(e.target.value)}
            InputLabelProps={{
              shrink: true,
            }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            İptal
          </Button>
          <Button onClick={handleReturnZimmet} color="primary">
            Geri Al
          </Button>
        </DialogActions>
      </Dialog>
      <Dialog open={newZimmetOpen} onClose={handleNewZimmetClose}>
        <DialogTitle>Yeni Zimmet Ekle</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Lütfen zimmetlenecek envanteri ve zimmet tarihini seçin.
          </DialogContentText>
          <Select
            labelId="envanter-select-label"
            id="envanter-select"
            value={selectedEnvanter}
            onChange={(e) => setSelectedEnvanter(e.target.value)}
            fullWidth
          >
            {availableEnvanter.map((envanter) => (
              <MenuItem key={envanter.envanterSeriNo} value={envanter.envanterSeriNo}>
                {envanter.envanterMarkasi} - {envanter.envanterModeli}
              </MenuItem>
            ))}
          </Select>
          <TextField
            margin="dense"
            id="envanterAlimTarihi"
            label="Zimmet Tarihi"
            type="date"
            fullWidth
            variant="outlined"
            value={envanterAlimTarihi}
            onChange={(e) => setEnvanterAlimTarihi(e.target.value)}
            InputLabelProps={{
              shrink: true,
            }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleNewZimmetClose} color="primary">
            İptal
          </Button>
          <Button onClick={handleCreateZimmet} color="primary">
            Zimmetle
          </Button>
        </DialogActions>
      </Dialog>
    </Paper>
  );
}
