import React, { useState, useEffect } from 'react';
import { Button, Paper } from '@mui/material';
import * as zimmetServices from '../services/zimmetServices';
import ZimmetSelect from './ZimmetSelect';


const ZimmetPage = () => {
  const [zimmetList, setZimmetList] = useState([]);
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedZimmet, setSelectedZimmet] = useState(null);
  const [page, setPage] = useState(0);
  const [pageSize, setPageSize] = useState(10);
  const [order, setOrder] = useState({ columnName: '', sort: true });
  const [isLoading, setIsLoading] = useState(true);

  const columns = [
    { field: 'zimmetId', headerName: 'Zimmet Id', minWidth: 170 },
    { field: 'statu', headerName: 'Statu', minWidth: 100 },
    { field: 'personelDto.personelSicilNo', headerName: 'Personel SicilNo', minWidth: 170 },
    { field: 'personelDto.personelName', headerName: 'Personel Adı', minWidth: 170 },
    { field: 'personelDto.personelSurname', headerName: 'Personel Soyadı', minWidth: 170 },
    { field: 'personelDto.personelBirimi', headerName: 'Personel Birimi', minWidth: 170 },
    { field: 'personelDto.personelGorevi', headerName: 'Personel Görevi', minWidth: 170 },
    { field: 'envanterDto.envanterTipi', headerName: 'Envanter Tipi', minWidth: 170 },
    { field: 'envanterDto.envanterMarkasi', headerName: 'Envanter Markası', minWidth: 170 },
    { field: 'envanterDto.envanterModeli', headerName: 'Envanter Modeli', minWidth: 170 },
    { field: '', headerName: 'Actions', minWidth: 170, renderCell: (row) => <Button onClick={() => handleOpenDialog(row)}>Düzenle</Button> },
  ];

  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      try {
        const zimmetResponse = await zimmetServices.getAllZimmet();
        setZimmetList(zimmetResponse.data);
      } finally {
        setIsLoading(false);
      }
    };
    fetchData();
  }, []);

  const handleOpenDialog = (zimmet = null) => {
    setSelectedZimmet(zimmet); // Yeni zimmet için null, düzenleme için mevcut zimmet
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setSelectedZimmet(null);
  };

  const handleSubmit = (newZimmet) => {
    zimmetServices.createZimmet(newZimmet).then(() => {
      zimmetServices.getAllZimmet().then(response => setZimmetList(response.data));
      handleCloseDialog();
    });
  };

  const handlePageChange = (event, newPage) => {
    setPage(newPage - 1); // Material-UI Pagination is 1-based, adjust to 0-based
  };

  const handleSortChange = (newOrder) => {
    setOrder(newOrder);
  };



  return (
    <>
      <Button variant="contained" color="primary" onClick={handleOpenDialog} style={{ margin: '20px' }}>
        Yeni Zimmet Ekle
      </Button>


      <ZimmetSelect
        open={openDialog}
        onClose={handleCloseDialog}
        onSubmit={handleSubmit}
      />
    </>
  );
};

export default ZimmetPage;
