import React, { useState, useEffect } from 'react';
import { Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import * as zimmetServices from '../services/zimmetServices';
import * as personelServices from '../services/personelServices';

const PersonelDetailPage = () => {
  const [personel, setPersonel] = useState(null);
  const [zimmetList, setZimmetList] = useState([]);
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedZimmet, setSelectedZimmet] = useState(null);
  const [sortDirection, setSortDirection] = useState('asc'); // 'asc' or 'desc'

  // URL'den personel sicil numarasını almak
  const url = window.location.href;
  const personelSicilNo = url.substring(url.lastIndexOf('/') + 1);

  useEffect(() => {
    // Personel bilgilerini al
    personelServices.getPersonelBySicilNo(personelSicilNo).then(response => {
      setPersonel(response.data);
    });

    // Zimmet bilgilerini al
    zimmetServices.getZimmetlerByPersonel(personelSicilNo).then(response => {
      const sortedList = sortZimmetListByModel(response.data, sortDirection);
      setZimmetList(sortedList);
    });
  }, [personelSicilNo, sortDirection]);

  const handleOpenDialog = (zimmet) => {
    setSelectedZimmet(zimmet);
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setSelectedZimmet(null);
  };

  const handleGeriAl = (zimmetId) => {
    zimmetServices.geriAlZimmet(zimmetId).then(() => {
      zimmetServices.getZimmetlerByPersonel(personelSicilNo).then(response => {
        const sortedList = sortZimmetListByModel(response.data, sortDirection);
        setZimmetList(sortedList);
      });
    });
  };

  const renderCellValue = (row, column) => {
    return row[column.id] || '';
  };

  const sortZimmetListByModel = (list, direction) => {
    return [...list].sort((a, b) => {
      const modelA = a.envanterDto?.envanterModel || '';
      const modelB = b.envanterDto?.envanterModel || '';

      return direction === 'asc'
        ? modelA.localeCompare(modelB)
        : modelB.localeCompare(modelA);
    });
  };

  const handleSort = (columnId) => {
    const newDirection = sortDirection === 'asc' ? 'desc' : 'asc';
    setSortDirection(newDirection);

    const sortedList = sortZimmetListByModel(zimmetList, newDirection);
    setZimmetList(sortedList);
  };

  return (
    <div>
      {personel && (
        <div>
          <h2>Personel Bilgileri</h2>
          <p><strong>Sicil No:</strong> {personel.personelSicilNo}</p>
          <p><strong>Ad:</strong> {personel.personelName}</p>
          <p><strong>Soyad:</strong> {personel.personelSurname}</p>
          <p><strong>Birim:</strong> {personel.personelBirimi}</p>
        </div>
      )}

      <Button variant="contained" color="primary" onClick={() => handleOpenDialog(null)} style={{ margin: '20px' }}>
        Yeni Zimmet Ekle
      </Button>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>
                Envanter Tipi
                <Button onClick={() => handleSort('envanterDto.envanterModel')} style={{ marginLeft: '10px' }}>
                  {sortDirection === 'asc' ? '▼' : '▲'}
                </Button>
              </TableCell>
              <TableCell>Envanter Markası</TableCell>
              <TableCell>Envanter Modeli</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {zimmetList.map((row) => (
              <TableRow key={row.zimmetId}>
                <TableCell>{row.envanterDto?.tip}</TableCell>
                <TableCell>{row.envanterDto?.envanterMarkasi}</TableCell>
                <TableCell>{row.envanterDto?.envanterModel}</TableCell>
                <TableCell>
                  <Button onClick={() => handleGeriAl(row.zimmetId)}>Geri Al</Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default PersonelDetailPage;
