import React, { useState } from 'react';
import { filterPersonel } from '../services/zimmetServices';
import ZimmetModal from './ZimmetModal'; // Import the modal component

const PersonelSearchPage = () => {
  const [sicilNo, setSicilNo] = useState('');
  const [name, setName] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [modalOpen, setModalOpen] = useState(false);
  const [selectedPersonel, setSelectedPersonel] = useState(null);

  const handleSearch = async () => {
    try {
      const response = await filterPersonel(sicilNo, name);
      setSearchResults(response.data);
    } catch (error) {
      console.error('Personel arama hatası:', error);
    }
  };

  const handleZimmetIslemleri = (personel) => {
    setSelectedPersonel(personel);
    setModalOpen(true);
  };

  const handleCloseModal = () => {
    setModalOpen(false);
    setSelectedPersonel(null);
  };

  return (
    <div>
      <h1>Personel Ara</h1>
      <div>
        <label>
          Sicil No:
          <input type="text" value={sicilNo} onChange={(e) => setSicilNo(e.target.value)} />
        </label>
        <label>
          Ad:
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </label>
        <button onClick={handleSearch}>Ara</button>
      </div>

      <table>
        <thead>
          <tr>
            <th>Sicil No</th>
            <th>Ad</th>
            <th>Soyad</th>
            <th>Birim</th>
            <th>İşlem</th>
          </tr>
        </thead>
        <tbody>
          {searchResults.map((personel) => (
            <tr key={personel.sicilNo}>
              <td>{personel.sicilNo}</td>
              <td>{personel.name}</td>
              <td>{personel.surname}</td>
              <td>{personel.birim}</td>
              <td>
                <button onClick={() => handleZimmetIslemleri(personel)}>Zimmet İşlemleri</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {modalOpen && selectedPersonel && (
        <ZimmetModal
          personelSicilNo={selectedPersonel.sicilNo}
          onClose={handleCloseModal}
        />
      )}
    </div>
  );
};

export default PersonelSearchPage;
