import "./App.css";
import LeftBar from "./containers/LeftBar";
import Content from "./containers/Content";
import Header from "./containers/Header";
import PersonelSearchPage from './PersonelYonetim/PersonelSearchPage';
import PersonelDetailPage from './PersonelYonetim/PersonelDetailPage';
import ZimmetDetailPage from './ZimmetYonetim/ZimmetDetailPage';
import Dashboard from './page/Dashboard';

function App() {
  let page;
  const path = window.location.pathname;

  if (path === '/personel-page') {
    page = <PersonelSearchPage />;
  } else if (path.startsWith('/personel/') && path.endsWith('/zimmetler')) {
    page = <ZimmetDetailPage />;
  } else if (path.startsWith('/personel/')) {
    page = <PersonelDetailPage />;
  } else {
    page = <Dashboard />;
  }

  return (
    <div style={{height:"100%"}}>
       <div className="App">
      <nav>
        <ul>
          <li><a href="/">Anasayfa</a></li>
          <li><a href="/personel-page">Personel Listesi</a></li>
        </ul>
      </nav>
      <div>{page}</div>
    </div>
      <Header id="header" />
      <LeftBar id="leftbar" />
      <Content id="content" />
    </div>
  );
}

export default App;
