import React, { useState } from 'react';
import { filterPersonel } from '../services/ZimmetService';

const PersonelSearchPage = () => {
  const [sicilNo, setSicilNo] = useState('');
  const [name, setName] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await filterPersonel(sicilNo, name);
      setSearchResults(response.data);
    } catch (error) {
      console.error('Personel arama hatası:', error);
    }
  };

  return (
    <div>
      <h1>Personel Ara</h1>
      <div>
        <label>
          Sicil No:
          <input type="text" value={sicilNo} onChange={(e) => setSicilNo(e.target.value)} />
        </label>
        <label>
          Ad:
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </label>
        <button onClick={handleSearch}>Ara</button>
      </div>
      {searchResults.length > 0 && (
        <div>
          <h2>Arama Sonuçları</h2>
          <table>
            <thead>
              <tr>
                <th>Sicil No</th>
                <th>Ad</th>
                <th>Soyad</th>
                <th>Birim</th>
                <th>İşlem</th>
              </tr>
            </thead>
            <tbody>
              {searchResults.map((personel) => (
                <tr key={personel.sicilNo}>
                  <td>{personel.sicilNo}</td>
                  <td>{personel.name}</td>
                  <td>{personel.surname}</td>
                  <td>{personel.birim}</td>
                  <td>
                    <a href={`/personel/${personel.sicilNo}`}>Zimmet İşlemleri</a>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default PersonelSearchPage;
