import React, { useState, useEffect } from 'react';
import { Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import * as personelServices from '../services/personelServices';
import * as zimmetServices from '../services/zimmetServices';

const PersonelDetailPage = () => {
  const [personelDetail, setPersonelDetail] = useState(null);
  const [zimmetList, setZimmetList] = useState([]);
  const [sortDirection, setSortDirection] = useState('asc'); // 'asc' or 'desc'
  const [sortBy, setSortBy] = useState('envanterModel'); // Column to sort by

  const path = window.location.pathname;
  const personelSicilNo = path.split('/')[2]; // Get Sicil No from URL

  useEffect(() => {
    // Fetch personel details
    personelServices.getPersonelBySicilNo(personelSicilNo).then(response => setPersonelDetail(response.data));

    // Fetch zimmet information for the personel
    zimmetServices.getZimmetlerByPersonel(personelSicilNo).then(response => setZimmetList(response.data));
  }, [personelSicilNo]);

  const handleGeriAl = (zimmetId) => {
    zimmetServices.geriAlZimmet(zimmetId).then(() => {
      zimmetServices.getZimmetlerByPersonel(personelSicilNo).then(response => setZimmetList(response.data));
    });
  };

  const columns = [
    { id: 'envanterDto.tip', label: 'Envanter Tipi', minWidth: 170 },
    { id: 'envanterDto.envanterMarkasi', label: 'Envanter Markası', minWidth: 170 },
    { id: 'envanterDto.envanterModel', label: 'Envanter Modeli', minWidth: 170 },
    { id: 'actions', label: 'Actions', minWidth: 170 },
  ];

  const renderCellValue = (row, column) => {
    return row[column.id] || '';
  };

  return (
    <div>
      <h1>Personel Detayı</h1>
      {personelDetail && (
        <div>
          <p><strong>Sicil No:</strong> {personelDetail.sicilNo}</p>
          <p><strong>Ad:</strong> {personelDetail.name}</p>
          <p><strong>Soyad:</strong> {personelDetail.surname}</p>
          <p><strong>Birim:</strong> {personelDetail.birim}</p>
          <a href={`/personel/${personelSicilNo}/zimmetler`}>
            <Button variant="contained" color="primary">Zimmet İşlemleri</Button>
          </a>
        </div>
      )}

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell key={column.id} style={{ minWidth: column.minWidth }}>
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {zimmetList.map((row) => (
              <TableRow key={row.zimmetId}>
                {columns.map((column) => (
                  <TableCell key={column.id}>
                    {column.id === 'actions' ? (
                      <Button onClick={() => handleGeriAl(row.zimmetId)} style={{ marginLeft: '10px' }}>
                        Geri Al
                      </Button>
                    ) : (
                      renderCellValue(row, column)
                    )}
                  </TableCell>
                ))}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default PersonelDetailPage;
