import React, { useState, useEffect } from 'react';
import { Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Dialog, DialogTitle, DialogContent, DialogActions, TextField, MenuItem, Select, InputLabel, FormControl } from '@mui/material';
import * as zimmetServices from '../services/zimmetServices';
import * as envanterServices from '../services/envanterServices';
import * as personelServices from '../services/personelServices';
import ZimmetSelect from './ZimmetSelect';

const ZimmetPage = () => {
  const [zimmetList, setZimmetList] = useState([]);
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedZimmet, setSelectedZimmet] = useState(null);
  const [envanterList, setEnvanterList] = useState([]);
  const [personelList, setPersonelList] = useState([]);

  const columns = [
    { id: 'zimmetId', label: 'Zimmet Id', minWidth: 170 },
    { id: 'statu', label: 'Statu', minWidth: 100 },
    { id: 'personelSicilNo', label: 'Personel SicilNo', minWidth: 170 },
    { id: 'personelName', label: 'Personel Adı', minWidth: 170 },
    { id: 'personelSurname', label: 'Personel Soyadı', minWidth: 170 },
    { id: 'personelBirimi', label: 'Personel Birimi', minWidth: 170 },
    { id: 'personelGorevi', label: 'Personel Görevi', minWidth: 170 },
    { id: 'envanterTipi', label: 'Envanter Tipi', minWidth: 170 },
    { id: 'envanterMarkasi', label: 'Envanter Markası', minWidth: 170 },
    { id: 'envanterModeli', label: 'Envanter Modeli', minWidth: 170 },
    { id: 'actions', label: 'Actions', minWidth: 170 },
  ];

  useEffect(() => {
    // Zimmet, envanter ve personel verilerini al
    zimmetServices.getAllZimmet().then(response => setZimmetList(response.data));
    envanterServices.getEnvanter().then(response => setEnvanterList(response.data));
    personelServices.getPersonel().then(response => setPersonelList(response.data));
  }, []);

  const handleOpenDialog = (zimmet) => {
    setSelectedZimmet(zimmet);
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setSelectedZimmet(null);
  };

  const handleSubmit = (newZimmet) => {
    zimmetServices.createZimmet(newZimmet).then(() => {
      // Yeniden verileri alarak güncellenmiş listeyi yükle
      zimmetServices.getAllZimmet().then(response => setZimmetList(response.data));
      handleCloseDialog();
    });
  };

  return (
    <>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell key={column.id} style={{ minWidth: column.minWidth }}>
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {zimmetList.map((row) => (
              <TableRow key={row.zimmetId}>
                {columns.map((column) => (
                  <TableCell key={column.id}>
                    {column.id === 'actions' ? (
                      <Button onClick={() => handleOpenDialog(row)}>Düzenle</Button>
                    ) : (
                      row[column.id]
                    )}
                  </TableCell>
                ))}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      {selectedZimmet && (
        <ZimmetSelect
          open={openDialog}
          onClose={handleCloseDialog}
          onSubmit={handleSubmit}
          envanterList={envanterList}
          personelList={personelList}
          selectedZimmet={selectedZimmet}
        />
      )}
    </>
  );
};

export default ZimmetPage;
