import React, { useState, useEffect } from 'react';
import { Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import axiosInstance from '../core/axiosInstance';

const ZimmetDetailPage = () => {
  const path = window.location.pathname;
  const personelSicilNo = path.split('/')[2];
  const [zimmetList, setZimmetList] = useState([]);

  useEffect(() => {
    const fetchZimmetler = async () => {
      try {
        const response = await axiosInstance.get(`/zimmet/personel/${personelSicilNo}/zimmet`);
        setZimmetList(response.data);
      } catch (error) {
        console.error('Zimmetleri getirme sırasında bir hata oluştu:', error);
      }
    };

    fetchZimmetler();
  }, [personelSicilNo]);

  const handleGeriAl = async (zimmetId) => {
    try {
      await axiosInstance.put(`/zimmet/${zimmetId}/geri-al`);
      const response = await axiosInstance.get(`/zimmet/personel/${personelSicilNo}/zimmet`);
      setZimmetList(response.data);
    } catch (error) {
      console.error('Geri alma işlemi sırasında bir hata oluştu:', error);
    }
  };

  return (
    <div>
      <h2>Zimmet Detayları - Sicil No: {personelSicilNo}</h2>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Envanter Tipi</TableCell>
              <TableCell>Envanter Markası</TableCell>
              <TableCell>Envanter Modeli</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {zimmetList.map((zimmet) => (
              <TableRow key={zimmet.zimmetId}>
                <TableCell>{zimmet.envanterDto.tip}</TableCell>
                <TableCell>{zimmet.envanterDto.envanterMarkasi}</TableCell>
                <TableCell>{zimmet.envanterDto.envanterModel}</TableCell>
                <TableCell>
                  <Button onClick={() => handleGeriAl(zimmet.zimmetId)}>Geri Al</Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default ZimmetDetailPage;
