import React, { useEffect, useState } from 'react';

const PersonelDetailPage = () => {
  const [personel, setPersonel] = useState(null);
  const personelSicilNo = window.location.pathname.split('/')[2];

  useEffect(() => {
    const fetchPersonel = async () => {
      const response = await fetch(`/api/personel/${personelSicilNo}`);
      const data = await response.json();
      setPersonel(data);
    };
    fetchPersonel();
  }, [personelSicilNo]);

  if (!personel) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Personel Detayları</h1>
      <p>Ad: {personel.name}</p>
      <p>Soyad: {personel.surname}</p>
      <p>Sicil No: {personel.sicilNo}</p>
      <p>Birim: {personel.birim}</p>
      <a href={`/personel/${personel.sicilNo}/zimmetler`}>Zimmetleri Gör</a>
    </div>
  );
};

export default PersonelDetailPage;
