import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, TextField } from '@mui/material';
import * as zimmetServices from '../services/zimmetServices';

const PersonelZimmetPage = () => {
  const { personelSicilNo } = useParams();
  const [zimmetList, setZimmetList] = useState([]);
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedZimmetId, setSelectedZimmetId] = useState(null);
  const [returnDate, setReturnDate] = useState('');

  useEffect(() => {
    zimmetServices.getZimmetlerByPersonel(personelSicilNo).then(response => {
      setZimmetList(response.data);
    });
  }, [personelSicilNo]);

  const handleGeriAl = (zimmetId) => {
    setSelectedZimmetId(zimmetId);
    setOpenDialog(true);
  };

  const handleDialogClose = () => {
    setOpenDialog(false);
    setSelectedZimmetId(null);
    setReturnDate('');
  };

  const handleReturnSubmit = () => {
    zimmetServices.geriAlZimmet(selectedZimmetId, returnDate).then(() => {
      zimmetServices.getZimmetlerByPersonel(personelSicilNo).then(response => {
        setZimmetList(response.data);
        handleDialogClose();
      });
    });
  };

  return (
    <div>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Envanter Tipi</TableCell>
              <TableCell>Envanter Markası</TableCell>
              <TableCell>Envanter Modeli</TableCell>
              <TableCell>İşlemler</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {zimmetList.map((zimmet) => (
              <TableRow key={zimmet.zimmetId}>
                <TableCell>{zimmet.envanterDto.tip}</TableCell>
                <TableCell>{zimmet.envanterDto.envanterMarkasi}</TableCell>
                <TableCell>{zimmet.envanterDto.envanterModel}</TableCell>
                <TableCell>
                  <Button variant="contained" color="secondary" onClick={() => handleGeriAl(zimmet.zimmetId)}>
                    Zimmeti Geri Al
                  </Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Dialog open={openDialog} onClose={handleDialogClose}>
        <DialogTitle>Zimmet Geri Alma</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Lütfen zimmeti geri alacağınız tarihi seçin.
          </DialogContentText>
          <TextField
            label="Geri Alma Tarihi"
            type="date"
            value={returnDate}
            onChange={(e) => setReturnDate(e.target.value)}
            InputLabelProps={{
              shrink: true,
            }}
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleDialogClose} color="primary">
            İptal
          </Button>
          <Button onClick={handleReturnSubmit} color="primary">
            Onayla
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default PersonelZimmetPage;
