import React, { useState, useEffect } from "react";
import * as services from "../services/personelServices";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TablePagination from "@mui/material/TablePagination";
import TableRow from "@mui/material/TableRow";
import Button from "@mui/material/Button";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import TextField from "@mui/material/TextField";

const columns = [
  { id: 'personelName', label: 'Personel Adı', minWidth: 170 },
  { id: 'personelSurname', label: 'Personel Soyadı', minWidth: 170 },
  { id: 'personelSicilNo', label: 'Personel SicilNo', minWidth: 170 },
  { id: 'personelBirimi', label: 'Personel Birimi', minWidth: 170 },
  { id: 'personelGorevi', label: 'Personel Görevi', minWidth: 170 },
  { id: 'actions', label: 'Actions', minWidth: 170 },
];

export default function PersonelPage() {
  const [personelList, setPersonelList] = useState([]);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [newPersonelOpen, setNewPersonelOpen] = useState(false);
  const [personelName, setPersonelName] = useState('');
  const [personelSurname, setPersonelSurname] = useState('');
  const [personelSicilNo, setPersonelSicilNo] = useState('');
  const [personelBirimi, setPersonelBirimi] = useState('');
  const [personelGorevi, setPersonelGorevi] = useState('');

  useEffect(() => {
    services.getPersonel().then((res) => {
      setPersonelList(res.data);
    });
  }, []);

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
  };

  const handleNewPersonelOpen = () => {
    setNewPersonelOpen(true);
  };

  const handleNewPersonelClose = () => {
    setNewPersonelOpen(false);
    setPersonelName('');
    setPersonelSurname('');
    setPersonelSicilNo('');
    setPersonelBirimi('');
    setPersonelGorevi('');
  };

  const handleCreatePersonel = () => {
    const newPersonel = {
      personelName,
      personelSurname,
      personelSicilNo,
      personelBirimi,
      personelGorevi,
    };
    // Personel oluşturma işlemi burada yapılacak
    services.createPersonel(newPersonel).then(() => {
      services.getPersonel().then((res) => {
        setPersonelList(res.data);
      });
    });
    handleNewPersonelClose();
  };

  return (
    <Paper sx={{ width: "100%", overflow: "hidden" }}>
      <Button variant="contained" color="primary" onClick={handleNewPersonelOpen}>
        Yeni Personel Ekle
      </Button>
      <TableContainer sx={{ maxHeight: 440 }}>
        <Table stickyHeader aria-label="sticky table">
          <TableHead>
            <TableRow>
              {columns.map((column) => (
                <TableCell
                  key={column.id}
                  align={column.align}
                  style={{ minWidth: column.minWidth }}
                >
                  {column.label}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {personelList
              .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
              .map((row) => (
                <TableRow hover role="checkbox" tabIndex={-1} key={row.personelSicilNo}>
                  {columns.map((column) => {
                    let value = row[column.id];
                    if (column.id === 'actions') {
                      return (
                        <TableCell key={column.id} align={column.align}>
                          {/* Actions, örneğin düzenleme veya silme butonları buraya eklenebilir */}
                        </TableCell>
                      );
                    }
                    return (
                      <TableCell key={column.id} align={column.align}>
                        {value}
                      </TableCell>
                    );
                  })}
                </TableRow>
              ))}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
        rowsPerPageOptions={[10, 25, 100]}
        component="div"
        count={personelList.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onPageChange={handleChangePage}
        onRowsPerPageChange={handleChangeRowsPerPage}
      />
      <Dialog open={newPersonelOpen} onClose={handleNewPersonelClose}>
        <DialogTitle>Yeni Personel Ekle</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Lütfen yeni personel bilgilerini girin.
          </DialogContentText>
          <TextField
            margin="dense"
            id="personelName"
            label="Personel Adı"
            type="text"
            fullWidth
            variant="outlined"
            value={personelName}
            onChange={(e) => setPersonelName(e.target.value)}
          />
          <TextField
            margin="dense"
            id="personelSurname"
            label="Personel Soyadı"
            type="text"
            fullWidth
            variant="outlined"
            value={personelSurname}
            onChange={(e) => setPersonelSurname(e.target.value)}
          />
          <TextField
            margin="dense"
            id="personelSicilNo"
            label="Personel Sicil No"
            type="text"
            fullWidth
            variant="outlined"
            value={personelSicilNo}
            onChange={(e) => setPersonelSicilNo(e.target.value)}
          />
          <TextField
            margin="dense"
            id="personelBirimi"
            label="Personel Birimi"
            type="text"
            fullWidth
            variant="outlined"
            value={personelBirimi}
            onChange={(e) => setPersonelBirimi(e.target.value)}
          />
          <TextField
            margin="dense"
            id="personelGorevi"
            label="Personel Görevi"
            type="text"
            fullWidth
            variant="outlined"
            value={personelGorevi}
            onChange={(e) => setPersonelGorevi(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleNewPersonelClose} color="primary">
            İptal
          </Button>
          <Button onClick={handleCreatePersonel} color="primary">
            Oluştur
          </Button>
        </DialogActions>
      </Dialog>
    </Paper>
  );
}
